  text-align: center;
  font-family: montserrat;
  width: 100vw;
  max-width: 1440px;
  height: 100vh;
  max-height: 1024px;
  position: relative;
  display: flex;
  flex-direction: column;
`

const Titre404 = styled.h1`
  color: ${colors.primary};
  font-size: 150px;
  margin: auto;

  @media sreen and (min-width: 375px) {
    font-size: 95px;
  }
`
const Texte404 = styled.span`
  color: ${colors.primary};
  display: block;
  font-size: 1.5rem;

  @media screen and(min-width: 768px) {
    font-size: 1.2rem;
  }
`

