import React, { useState } from 'react'
import { useEffect } from 'react'
import arrowup from '../../assets/arrowup.svg'
import arrowdown from '../../assets/arrowdown.svg'
import * as style from './style.module.css'

class DropDownCollapse extends React.Component {
  constructor(props) {
    super(props)

    this.state = { isOpen: false }
    this.updateIsOpen = this.updateIsOpen.bind(this)
  }

  updateIsOpen() {
    this.setState({ isOpen: !this.state.isOpen })
  }

  render() {
    const collapseArrow = `arrow-${this.state.isOpen ? 'up' : 'down'}`

    return (
      <section
        className={`{style.dropdowncontainer}${
          this.state.isOpen ? 'isopen' : 'isclose'
        }`}
      >
        <h2 className={style.dropdowntitle} onClick={this.updateIsOpen}>
          <span>{this.props.title}</span>
          <span className={collapseArrow}></span>
        </h2>

        <ul
          className={`{style.dropdowntext} ${
            this.state.isOpen ? 'isopen' : 'isclose'
          }`}
        >
          {this.props.textArray.map((item, index) => (
            <li key={`item-${index}`}>{item}</li>
          ))}
        </ul>
      </section>
    )
  }
}

export default DropDownCollapse
