import styled from 'styled-components'

const CardTitle = styled.div`
  color: #ffffff;
  font-size: 22px;
  font-weight: medium;
  height: 25px;
  display: flex;
  align-items: center;
`

const CardImage = styled.img`
  height: 150px;
  width: 150px;
  align-self: center;
  border-radius: 50%;
`

const CardWrapper = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  padding: 15px;
  background-color: #f6f6f6;
  border-radius: 10px;
  width: 340px;
  height: 340px;
  &:hover {
    cursor: pointer;
  }
`
const logements = this.props.logements
const baseUrl = '../../assets/logements.json'
const fetchLogements = []
for (let key in logements) {
  fetchLogements.push({
    ...logements[key],
  })
}
const listeLogements = fetchLogements.map((product, index) => {
  return (
    <Image
      key={index}
      imageSource={baseUrl + logements.image}
      title={logements.title}
    />
  )
})

