  text-align: center;
  font-family: montserrat;
  width: 100vw;
  max-width: 1440px;
  height: 100vh;
  max-height: 1024px;
  position: relative;
  display: flex;
  flex-direction: column;
`

const Titre404 = styled.h1`
  color: ${colors.primary};
  font-size: 150px;

  @media (max-width: 375px) {
    font-size: 95px;
  }
`
const Texte404 = styled.span`
  color: ${colors.primary};
  display: block;
  font-size: 1.5rem;

  @media (max-width: 375px) {
    font-size: 1.2rem;
  }
`

export const Navlink = styled(Link)`
  color: ${colors.primary};
  text-decoration: none;
`

