import React from 'react'
import { Link } from 'react-router-dom'
import styled from 'styled-components'

const CardWrapper = styled(Link)`
  width: 340px;
  height: 340px;
  border-radius: 10px;
  margin: 10px;
  transition: 200ms;
  position: relative;

  &:hover {
    cursor: pointer;
    box-shadow: 2px 2px 8px #e2e3e9;
    > CardImage {
      opacity: 0.3;
    }
  }
`
const CardImage = styled.img`
  height: 340px;
  width: 340px;
  position: relative;
  object-fit: cover;
  align-self: center;
  border-radius: 10px;
`
const CardOmbre = styled.div`
  box-shadow: 2px 2px 8px #e2e3e9;
  border-radius: 10px;
  height: 100%;
  left: 0;
  position: absolute;
  top: 0;
  width: 100%;
`

const CardTitle = styled.p`
  position: absolute;
  left: 20px;
  bottom: 20px;
  margin-right: 20px;
  color: #ffffff;
  font-family: montserrat;
  font-size: 18px;
  font-weight: 500;
`

const Card = ({ picture, title }) => {
  return (
    <CardWrapper>
      <CardImage src={picture} alt={title} />
      <CardOmbre></CardOmbre>
      <CardTitle>{title}</CardTitle>
    </CardWrapper>
  )
}

export default Card
