import React, { useState, useEffect } from 'react'
import styled from 'styled-components'
import arrowForwardCarrousel from '../../assets/arrowForwardCarrousel.svg'
import arrowBackwardCarrousel from '../../assets/arrowBackwardCarrousel.svg'
import { Slide } from 'react-slideshow-image'
import { useParams } from 'react-router'
import logements from '../../assets/logements.json'

const ImageLogement = styled.img`
  width: 1240px;
  height: 415px;
`
const Arrow = styled.button`
  width: 46px;
  height: 79px;
`
const Carrousel = (props) => {
  const { id } = props.match.params
  const [slides, setSlides] = useState([])

  useEffect(() => {
    const filterSlides = logements.find((logement) => logement.id === id).slides
    setSlides(filterSlides.pictures)
  }, [])
  return (
    <div>
      {slides.length &&
        slides.map((picture, index) => (
          <p key={index} src={`${picture}`}>
            {' '}
            {picture.url}
          </p>
        ))}
    </div>
  )
}
export default Carrousel
