import React, { useState, useEffect } from 'react'
import * as style from './style.module.css'

export default function Carrousel(props) {
  const [slideIndex, setSlideIndex] = useState(0)
  const picturesIndx = () => {
    const carrouselPics = document.querySelector('.carrousel-container img')
    if (!carrouselPics) {
      return 0
    }
    return carrouselPics.clientWidth
  }

  const nextPic = () => {
    if (slideIndex === props.img.length - 1) {
      setSlideIndex(0)
    } else {
      setSlideIndex(slideIndex + 1)
    }
  }
  const prevPic = () => {
    if (slideIndex === 0) {
      setSlideIndex(props.img.length - 1)
    } else {
      setSlideIndex(slideIndex - 1)
    }
  }

  return (
    <div className={style.carrousel}>
      <div
        className={style.carrousel - container}
        style={{ transform: `translateX(-${slideIndex * carrouselPics()}px)` }}
      >
        {props.img.map((picture) => (
          <img className={style.carrouselPicture} src={picture} key={picture} />
        ))}
      </div>
    </div>
  )
}
