import React, { useState } from 'react'
import styled from 'styled-components'
import colors from '../../utils/style/colors'
import arrow from '../../assets/arrow.svg'
import * as style from './style.module.css'

function DropdownCollapse({ titre, description }) {
  /* Crée un Hook d'état */
  const [open, setOpen] = useState(false)

  return (
    <div className={style.dropdowncontainer} id={`DropDown${titre}`}>
      <h2 classname={style.dropdownheader}>
        <h4 classname={style.dropdowntitle}>{titre}</h4>
        <span
          classname={style.dropdownarrow}
          open={open}
          onClick={() => setOpen(!open)}
          isOpen={open === true}
        >
          <img src={arrow} alt="En savoir plus" />
        </span>
      </h2>
      {
        /* Si le dropdown est à TRUE alors il affichera la description */
        open && <ul className={style.dropdowntext}>{description}</ul>
      }
    </div>
  )
}

export default DropdownCollapse
