import React from 'react'
import cx from 'classnames'
import Collapse from 'react-collapse'

import styled from 'styled-components'

const DropDownContainer = styled.div`
  margin: 0 auto;
  font-size: 1.6rem;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  max-width: 500px;
  padding: 3rem 1rem 1rem;
`
const CollapsedBloc = styled.div`
  width: 100%;
  display: block;
  position: relative;
  overflow: hidden;
  transition: height 300ms cubic-bezier(0.4, 0, 0.2, 1);

 
    &:after {
      pointer-events: none;
      transition: opacity 300ms;
      will-change: opacity;
      opacity: 1;
      content: "";
      display: block;
      position: absolute;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      background: linear-gradient(transparent 50%, white);
      ${(props) => (props.isOpen1 ? '&:after{ opacity: 0;}' : '')}
    }
    }
  
`

const DropDownBtn = styled.button`
  display: block;
  width: 20rem;
  max-width: 100%;
  margin: 0 auto;
  padding: 1rem;
`

const DropDownTitle = styled.p`
  width: 100%;
  color: #ffffff;
  padding-left: 10px;
  font-family: montserrat;
  font-size: 24px;
  font-weight: 500;
`
const DropDownArrow = styled.select`
  background-color: ${colors.primary};
  border: none;
  color: #ffffff;
  padding-right: 10px;
  width: 29px;
  height: 29px;
`

class DropDownCollapse extends React.Component {
  constructor(props) {
    super(props)
    this.state = { isOpen1: false }
  }

  render() {
    return (
      <DropDownContainer>
        <DropDownBtn
          className={cx('DropDownContainer__toggle', {
            'DropDownContainer__toggle--active': this.state.isOpen1,
          })}
          onClick={() => this.toggle(1)}
        >
          <DropDownTitle>toggle</DropDownTitle>
          <DropDownArrow>
            <svg
              className={('icon', { 'icon--expanded': this.state.isOpen1 })}
              viewBox="6 0 12 24"
            >
              <polygon points="8 0 6 1.8 14.4 12 6 22.2 8 24 18 12" />
            </svg>
          </DropDownArrow>
        </DropDownBtn>

        <CollapsedBloc
          isOpen={this.state.isOpen1}
          collapseActive={this.state.isOpen1 ? CollapsActive : ''}
        >
          <DropDownText>
            Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do
            eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim
            ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut
            aliquip ex ea commodo consequat.
            <button onClick={() => this.toggle(1)} className="app__button">
              close
            </button>
          </DropDownText>
        </CollapsedBloc>
      </DropDownContainer>
    )
  }

  toggle = (index) => {
    let collapse = 'isOpen' + index

    this.setState((prevState) => ({ [collapse]: !prevState[collapse] }))
  }
}
export default DropDownCollapse
