import Banner from '../../components/Banner'
import styled from 'styled-components'
import { Link } from 'react-router-dom'
import Card from '../../components/Card'
import logements from '../../assets/logements.json'

const HomeContainer = styled.div`
  display: flex;
  flex-direction: column;
  width: 100vw;
  height: 100vh;
  max-height: 1095px;
  color: F6F6F6;
`
const CardsContainer = styled.div`
  width: 90vw;
  max-width: 1240px;
  height: 90vh;
  max-height: 829px;
  display: flex;
  align-items: space-between;
  color: #f6f6f6;
`
function Home() {
  const thumbs = logements.reduce(
    (acc, logement) =>
      acc.includes(logement.id) ? acc : acc.concat(logement.id),
    []
  )
  return (
    <HomeContainer>
      <Banner />
      <CardsContainer>
        {thumbs.map((logement, _id) => (
          <Link key={{ logement }}>
            <Card picture={logement.cover} title={logement.title} />
          </Link>
        ))}
      </CardsContainer>
    </HomeContainer>
  )
}

export default Home
