import styled from 'styled-components'
import logements from '../../assets/logements.json'

const CardBackground = styled.div`
  width: 340px;
  heigt: 340px;
  color: #ff6060;
  border-radius: 10px;
  display: flex;
  flex-direction: column;
`

const CardTitle = styled.div`
  color: #ffffff;
  font-size: 22px;
  font-weight: medium;
  height: 25px;
  display: flex;
  align-items: center;
`

const CardImage = styled.img`
  height: 340px;
  width: 340px;
  align-self: center;
  border-radius: 10px;
`

const CardWrapper = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  padding: 15px;
  background-color: #f6f6f6;
  border-radius: 10px;
  width: 340px;
  height: 340px;
  &:hover {
    cursor: pointer;
  }
`
function Card() {
  const thumbs = logements.reduce(
    (acc, logement) =>
      acc.includes(logement.id) ? acc : acc.concat(logement.id),
    []
  )
  return (
    <CardBackground>
      {thumbs.map((thumb) => (
        <CardWrapper key={thumb}>
          <CardImage key={thumb} src={thumb.cover} alt="logementS" />
          <CardTitle key={thumb}>{thumb.title}</CardTitle>
        </CardWrapper>
      ))}
    </CardBackground>
  )
}
export default Card
