import React, { useState } from 'react'
import * as style from './style.module.css'
import arrow from '../../assets/arrowForwardCarrousel.svg'
import arrowback from '../../assets/arrowBackwardCarrousel.svg'

export default function Carrousel(props) {
  const [slideIndex, setSlideIndex] = useState(0)
  const picturesIndx = () => {
    const carrouselPics = document.querySelector('.carrouselcontainer img')
    if (!carrouselPics) {
      return 0
    }
    return carrouselPics.width
  }

  const nextPic = (currentIndex) => {
    if (slideIndex === 0) {
      setSlideIndex(props.pictures.length + 1)
    } else {
      setSlideIndex(slideIndex - 1)
    }
  }

  const prevPic = (currentIndex) => {
    if (slideIndex === 0) {
      setSlideIndex(props.pictures.length - 1)
    } else {
      setSlideIndex(slideIndex + 1)
    }
  }

  return (
    <div className={style.carrousel}>
      <div
        className={style.carrouselcontainer}
        style={{ transform: `translateX(-${slideIndex * picturesIndx()}px)` }}
      >
        {props.pictures.map((picture) => (
          <img className={style.carrouselPicture} src={picture} key={picture} />
        ))}
      </div>
      <div className={style.arrows}>
        <img src={arrowback} className={style.arrowback} onClick={nextPic} />
        <img src={arrow} className={style.arrow} onClick={prevPic} />
      </div>
      <div className={style.carrouselindex}>
        {slideIndex + 1} / {props.pictures.length}
      </div>
    </div>
  )
}
