import styled from 'styled-components'
import logements from '../../assets/logements.json'

const CardTitle = styled.div`
  color: #ffffff;
  font-size: 22px;
  font-weight: medium;
  height: 25px;
  display: flex;
  align-items: center;
`

const CardImage = styled.img`
  height: 150px;
  width: 150px;
  align-self: center;
  border-radius: 50%;
`

const CardWrapper = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  padding: 15px;
  background-color: #f6f6f6;
  border-radius: 10px;
  width: 340px;
  height: 340px;
  &:hover {
    cursor: pointer;
  }
`
function Card() {
  return (
    <CardWrapper>
      <div classname="inner">
        {logements &&
          logements.map((logement) => {
            return (
              <div className="cardImage" key={logement.id}>
                <img src={logement.cover} alt="logement" />

                {logement.title}
              </div>
            )
          })}
      </div>
    </CardWrapper>
  )
}
export default Card
