import React, { useState } from 'react'
import { useEffect } from 'react'
import arrow from '../../assets/arrow.svg'
import * as style from './style.module.css'

function DropdownCollapse({ titre, description }) {
  /* Crée un Hook d'état */
  const [isOpen, setIsOpen] = useState(false)
  const [style, setStyle] = useState(`${style.dropdownarrow}`)
  const changeStyle = () => {
    setStyle(`${style.dropdownarrowisOpen}`)
  }
  return (
    <section
      className={style.dropdowncontainer}
      id={`DropDown${titre}`}
      onClick={() => setIsOpen(!isOpen)}
    >
      <div className={style.dropdownheader}>
        <h2 className={style.dropdowntitle}>{titre}</h2>
        <span className={style.dropdownarrow} onChange={changeStyle}>
          <img src={arrow} alt="En savoir plus" />
        </span>
      </div>
      {
        /* Si le dropdown est à TRUE alors il affichera la description */
        isOpen && <div className={style.dropdowntext}>{description}</div>
      }
    </section>
  )
}

export default DropdownCollapse
