import React, { useState } from 'react'
import * as style from './style.module.css'
import arrow from '../../assets/arrowForwardCarrousel.svg'
import arrowback from '../../assets/arrowBackwardCarrousel.svg'

export default function Carrousel(props) {
  const [slideIndex, setSlideIndex] = useState(0)

  const indexPics = props.pictures.length

  const picturesIndx = () => {
    const carrouselPics = document.querySelector('.carrouselcontainer img')
    if (!carrouselPics) {
      return 0
    }
    return carrouselPics.width
  }

  const prevPic = () => {
    if (slideIndex === 0) {
      setSlideIndex(indexPics - 1)
    } else {
      setSlideIndex(slideIndex - 1)
    }
    return setSlideIndex
  }

  const nextPic = () => {
    if (slideIndex === indexPics - 1) {
      setSlideIndex((indexPics = 0))
    } else {
      setSlideIndex(slideIndex + 1)
    }
    return setSlideIndex
  }

  return (
    <div className={style.carrousel}>
      {indexPics > 1 && (
        <img
          className={arrowback}
          src={arrowback}
          alt="Image prédédente"
          onClick={prevPic}
        />
      )}

      {props.pictures.map((picture, index) => {
        const active = index === setSlideIndex
        return (
          <img
            className={`{style.carrouselpicture} ${active} ? ${style.carrouselpictureactive} : {}}`}
            src={picture}
          />
        )
      })}
      {indexPics > 1 && (
        <img
          className={arrow}
          src={arrow}
          alt="Image suivante"
          onClick={nextPic}
        />
      )}
    </div>
  )
}
