import { Component } from 'react'
import styled from 'styled-components'
import propTypes from 'prop-types'
import logements from '../../assets/logements.json'
import { Link } from 'react-router-dom'

const CardTitle = styled.p`
  position: relative;
  top: -25%;
  left: 2%;
  color: #ffffff;
  font-family: montserrat;
  font-size: 22px;
  font-weight: medium;
  height: 25px;
  z-index: 2;
`

const CardImage = styled.img`
  height: 340px;
  width: 340px;
  position: relative;
  object-fit: cover;
  align-self: center;
  border-radius: 10px;
  z-index: 1;
`

const CardWrapper = styled.div`
  width: 340px;
  height: 340px;

  border-radius: 10px;
  background: rgb(11, 11, 11);
  background: linear-gradient(
    0deg,
    rgba(11, 11, 11, 1) 0%,
    rgba(255, 255, 255, 0.006711409395973145) 50%
  );
  z-index: 0;
  &:hover {
    cursor: pointer;
  }
`
const CardContainer = styled.section`
  width: 1440px;
  height: 1095px;
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  justify-content: space-around;
`
function Card() {
  return (
    <CardContainer>
      {logements &&
        logements.map((logement) => {
          return (
            <Link key={logement.id}>
              <CardWrapper>
                key={logement.id}
                picture={logement.cover}
                title={logement.title}
              </CardWrapper>
            </Link>
          )
        })}
    </CardContainer>
  )
}

export default Card
