import styled from 'styled-components'
import logements from '../../assets/logements.json'

const CardTitle = styled.div`
  color: #ffffff;
  font-size: 22px;
  font-weight: medium;
  height: 25px;
  display: flex;
  align-items: center;
`

const CardImage = styled.img`
  height: 150px;
  width: 150px;
  align-self: center;
  border-radius: 10px;
`

const CardWrapper = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  padding: 15px;
  background-color: #f6f6f6;
  border-radius: 10px;
  width: 340px;
  height: 340px;
  &:hover {
    cursor: pointer;
  }
`
function Card() {
  return (
    <CardWrapper>
      <CardImage src={cover} />
      <CardTitle>{title}</CardTitle>
    </CardWrapper>
  )
}
export default Card
